import { Component } from '@angular/core';
import { CartService } from '../cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {

  items = this.cartService.getItems();

  constructor(
    private cartService: CartService
  ) { }

}

<h2 class="cart-component-title">MY SHOPPING CART</h2>

<div class="cart-component-item-wrapper">
  <div class="cart-component-item" *ngFor="let item of items">
    <span class="cart-component-item-name">{{ item.name }}</span>
    <span class="cart-component-item-price">{{ item.price | currency }}</span>
  </div>
</div>

<p routerLink="/shipping" class="cart-component-shipping-link">Shipping Prices</p>
