import { Component } from '@angular/core';
import { mensproducts } from '../mensproducts';

@Component({
  selector: 'app-men',
  templateUrl: './men.component.html',
  styleUrls: ['./men.component.css']
})
export class MenComponent {

  mensproduct = [...mensproducts]

}
<h2 class="men-title">MENS PRODUCTS</h2>

<div *ngFor="let mensproduct of mensproduct" class="men-product">
  <h5 class="men-product-name">
    <a 
      [title]="mensproduct.name + ' details'"
      [routerLink]="['/mens', mensproduct.id]"
      class="men-product-link">
      {{ mensproduct.name }}
    </a>
  </h5>

  <img [src]="mensproduct.imageUrl" class="men-product-image men-product-link" alt="{{ mensproduct.name }} Image"
       [routerLink]="['/mens', mensproduct.id]">

  <p *ngIf="mensproduct.description" class="men-product-description">{{ mensproduct.description }}</p>
</div>
