import { Component } from '@angular/core';
import { sportsproducts } from '../sportsproducts';



@Component({
  selector: 'app-sportsinfo',
  templateUrl: './sportsinfo.component.html',
  styleUrls: ['./sportsinfo.component.css']
})
export class SportsinfoComponent {

  sportsproduct = [...sportsproducts];

  share() {
    window.alert('The product has been shared!');
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale!');
  }
}

<h2>SPORTS PRODUCTS</h2>

<div class="product-container">
  <div *ngFor="let sportsproduct of sportsproduct" class="product">
    <h3>
      <a [title]="sportsproduct.name + ' details'" [routerLink]="['/products', sportsproduct.id]">
        {{ sportsproduct.name }}
      </a>
    </h3>
    <img [src]="sportsproduct.imageUrl" alt="{{ sportsproduct.name }} Image" [routerLink]="['/products', sportsproduct.id]">
    <p *ngIf="sportsproduct.description">{{ sportsproduct.description }}</p>
  </div>
</div>
